public getDiscountedPrice(listItem: any) {
  const temp = (listItem.originalPrice / 100) * listItem.discountPrice;
  return listItem.originalPrice - temp;
}
public getPrice() {
  this.listItem.forEach(item => {
    if (item.originalPrice > 0) {
        item.price = this.getDiscountedPrice(item);
    }
  });
}
/***************Add qty*****************/
public addQtyService(listItem: any) {
    this.listItem.forEach(item => {
      if (item.itemId === listItem.itemId) {
        if (item.qty > 0) {
          item.qty = item.qty - 1;
          item.qtyUsed = item.originalQty - item.qty;
          this.getAddedListService(item);
      }
      }
    });
    this.getItems();
    this.cartCountUpdate(true);
}
/**************Subtract qty********************/
public subQtyService(listItem: any) {
  this.listItem.forEach(item => {
    if (item.itemId === listItem.itemId) {
      if (item.qtyUsed > 0) {
        item.qty = item.qty + 1;
        item.qtyUsed = item.qtyUsed - 1;
        this.getRemovedListService(item, true);
    }
    }
  });
  this.getItems();
  this.cartCountUpdate(false);
}
/*******************Remove item**********************/
public removeService(listItem: any) {
  let itemQtyUsed = 0;
  this.listItem.forEach(item => {
    if (item.itemId === listItem.itemId) {
      if (item.qtyUsed > 0) {
        item.qty = item.qty + listItem.qtyUsed;
        itemQtyUsed = listItem.qtyUsed;
        item.qtyUsed = 0;
        this.cartCountUpdate(false, itemQtyUsed);
        this.getRemovedListService(item, false);
    }
    }
  });
  this.getItems();
}
public cartCountUpdate(isAdd: boolean, qtyUsed?: number) {
  if (!this.cartCounter) {
    this.cartCounter = 0;
  }
  if (isAdd) {
    GlobalConstants.cartCounter = GlobalConstants.cartCounter + 1;
  } else {
    if (qtyUsed) {
      if (GlobalConstants.cartCounter > 0) {
        GlobalConstants.cartCounter = GlobalConstants.cartCounter - qtyUsed;
      }
    } else {
      if (GlobalConstants.cartCounter > 0) {
        GlobalConstants.cartCounter = GlobalConstants.cartCounter - 1;
      }
    }
  }
  this.totalCartItems.next(GlobalConstants.cartCounter);
}
/*********************Push to list***************************/
public getAddedListService(listItem) {
   if (GlobalConstants.mycartDetails.length === 0) {
    GlobalConstants.mycartDetails.push(listItem);
  } else if (GlobalConstants.mycartDetails.length >= 0) {
    let isNew = false;
    for (const elem of GlobalConstants.mycartDetails) {
      if (elem.itemId === listItem.itemId) {
          isNew = true;
          elem.qtyUsed = elem.originalQty - listItem.qty;
          break;
      }
    }
    if (!isNew) {
      GlobalConstants.mycartDetails.push(listItem);
    }
  }
   this.cartDetails.next(GlobalConstants.mycartDetails);
   this.getPaymentDetails();
}
/*********************Remove from list***************************/
public getRemovedListService(listItem, isDeduct) {
  if (GlobalConstants.mycartDetails.length >= 0) {
   for (const elem of GlobalConstants.mycartDetails) {
     if (isDeduct) {
      if (elem.itemId === listItem.itemId) {
        elem.qtyUsed = listItem.qtyUsed;
        elem.qty = listItem.qty;
        if (elem.qtyUsed === 0) {
           GlobalConstants.mycartDetails = GlobalConstants.mycartDetails.filter(item => {
              return item.itemId !== listItem.itemId;
            });
        }
    }
     }
     else {
        GlobalConstants.mycartDetails = GlobalConstants.mycartDetails.filter(item => {
        return item.itemId !== listItem.itemId;
      });
     }
   }
 }
  this.cartDetails.next(GlobalConstants.mycartDetails);
  this.getPaymentDetails();
}
public getPaymentDetails() {
  GlobalConstants.paymentDetails = [];
  if (GlobalConstants.mycartDetails.length > 0) {
    GlobalConstants.mycartDetails.forEach(elem => {
          GlobalConstants.paymentDetails.push(elem);
    });
    this.getTotalPayable();
    this.totalPayableAmount.next(GlobalConstants.totalPayableAmount);
    this.totalDiscountedAmount.next(GlobalConstants.discountedPrice);
  } else if (GlobalConstants.mycartDetails.length === 0) {
    this.totalPayable = 0;
    this.discountedPrice = 0;
    GlobalConstants.totalPayableAmount = this.totalPayable;
    GlobalConstants.discountedPrice = this.discountedPrice;
    this.totalPayableAmount.next(GlobalConstants.totalPayableAmount);
    this.totalDiscountedAmount.next(GlobalConstants.discountedPrice);
  }
}
public getTotalPayable() {
  if (GlobalConstants.paymentDetails.length > 0) {
    GlobalConstants.paymentDetails.forEach(item => {
        this.totalPayable = this.totalPayable + (item.price * item.qtyUsed);
        this.discountedPrice = this.discountedPrice + ((item.originalPrice - item.price) * item.qtyUsed);
    });
    GlobalConstants.totalPayableAmount = this.totalPayable;
    GlobalConstants.discountedPrice = this.discountedPrice;
    this.paymentDetails.next(GlobalConstants.paymentDetails);
    this.resetFields();
  } else if (GlobalConstants.paymentDetails.length === 0) {
    this.resetFields();
    GlobalConstants.totalPayableAmount = this.totalPayable;
    GlobalConstants.discountedPrice = this.discountedPrice;
    this.paymentDetails.next(GlobalConstants.paymentDetails);
  }
}
public resetFields() {
  this.totalPayable = 0;
  this.discountedPrice = 0;
}
public searchService(searchField: any) {
  if (searchField) {
    let filteredList = [];
    filteredList = this.listItem.filter((item) => item.name.toLowerCase().startsWith(searchField));
    this.shoppingDetails.next(filteredList);
}
}
public sortedHighPriceService() {
  const highPricedList = this.listItem;
  highPricedList.sort((a, b) => b.price - a.price);
  this.shoppingDetails.next(highPricedList);
}
public sortedLowPriceService() {
  const highPricedList = this.listItem;
  highPricedList.sort((a, b) => a.price - b.price);
  this.shoppingDetails.next(highPricedList);
}
public sortedDiscountItemsService() {
  const highPricedList = this.listItem;
  highPricedList.sort((a, b) => (b.originalPrice - b.price) - (a.originalPrice - a.price));
  this.shoppingDetails.next(highPricedList);
}
